import React, { useState, useEffect } from 'react';
import { ControleLivros } from './controle/ControleLivros';
import { ControleEditora } from './controle/ControleEditora';

function LivroLista() {
    const [livros, setLivros] = useState([]);
    const [carregado, setCarregado] = useState(false);
    const controleLivros = new ControleLivro();

    useEffect(() => {
        if (!carregado) {
            setLivros(controleLivros.obterLivros());
            setCarregado(true);
        }
    }, [carregado, controleLivros]);

    const excluir = (codigoLivro) => {
        controleLivros.excluir(codigoLivro);
        setCarregado(false);
    };

    return (
        <main>
            <h1 style={{ textAlign: 'center' }}>Título e Capa: O catálogo lista três livros relacionados à programação em Java</h1>
            {livros.map((livro) => (
                <div key={livro.codigo} style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
                    <h2>{livro.titulo}</h2>
                    <p><strong>Editora:</strong> {livro.codEditora}</p>
                    <p><strong>Resumo:</strong> {livro.resumo}</p>
                    <p><strong>Autores:</strong> {livro.autores.join(', ')}</p>
                    <button onClick={() => excluir(livro.codigo)}>Excluir</button>
                </div>
            ))}
        </main>
    );
}

export default LivroLista;
