import React, { useState } from "react";

const LivroLista: React.FC = () => {
  const [livros, setLivros] = useState<string[]>([
    "Ice Breaker",
    "I'm glad my mom died",
    "Hopeless",
    "Todas as suas imperfeições",
    "Nunca jamais",
  ]);

  const excluirLivro = (livro: string) => {
    const novaLista = livros.filter((item) => item !== livro);
    setLivros(novaLista);
  };

  return (
    <ul>
      {livros.map((livro, index) => (
        <li key={index}>
          {livro}
          <button onClick={() => excluirLivro(livro)}>Excluir</button>
        </li>
      ))}
    </ul>
  );
};

export default LivroLista;
