import React, { useState, useEffect } from "react";
import { ControleLivro } from "./controle/ControleLivros";
import { ControleEditora } from "./controle/ControleEditora";

function LinhaLivro({ livro, excluir }) {
  const controleEditora = new ControleEditora();
  const nomeEditora = controleEditora.getNomeEditora(livro.codEditora);

  return (
    <tr>
      <td>
        <div>
          <h5>{livro.titulo}</h5>
          <button
            className="btn btn-danger"
            onClick={() => excluir(livro.codigo)}
          >
            Excluir
          </button>
        </div>
        <div>
          <p>
            <strong>Editora:</strong> {nomeEditora}
          </p>
          <p>
            <strong>Resumo:</strong> {livro.resumo}
          </p>
          <p>
            <strong>Autores:</strong> {livro.autores.join(", ")}
          </p>
        </div>
      </td>
    </tr>
  );
}

export default function LivroLista() {
  const [livros, setLivros] = useState([]);
  const [carregado, setCarregado] = useState(false);
  const controleLivros = new ControleLivro();

  useEffect(() => {
    if (!carregado) {
      setLivros(controleLivros.obterLivros());
      setCarregado(true);
    }
  }, [carregado, controleLivros]);

  const excluir = (codigoLivro) => {
    controleLivros.excluir(codigoLivro);
    setCarregado(false);
  };

  return (
    <main>
      <h1>Lista de Livros</h1>
      <table className="table">
        <tbody>
          {livros.map((livro) => (
            <LinhaLivro key={livro.codigo} livro={livro} excluir={excluir} />
          ))}
        </tbody>
      </table>
    </main>
  );
}
